import React, { useState, useEffect } from 'react';
import { Table, TableHead, TableBody, TableRow, TableCell, TextField } from '@material-ui/core';

function ExpenseTable() {
    const [expenses, setExpenses] = useState([]);
    const [filteredExpenses, setFilteredExpenses] = useState([]);
    const [filters, setFilters] = useState({
        userId: '',
        type: '',
        startDate: '',
        endDate: ''
    });

    useEffect(() => {
        // Fetch expenses from backend API and setExpenses
        fetchExpenses();
    }, [filters]); // Call fetchExpenses whenever the filters state changes


    useEffect(() => {
        // Apply filters to expenses and update filteredExpenses
        const filtered = expenses.filter(expense => {
            // Implement your filter logic here
            // Example:
            return (
                (!filters.userId || expense.userId === parseInt(filters.userId)) &&
                (!filters.type || expense.type === filters.type) &&
                (!filters.startDate || new Date(expense.date) >= new Date(filters.startDate)) &&
                (!filters.endDate || new Date(expense.date) <= new Date(filters.endDate))
            );
        });
        setFilteredExpenses(filtered);
    }, [expenses, filters]);

    const handleFilterChange = (e) => {
        const { name, value } = e.target;
        setFilters(prevFilters => ({
            ...prevFilters,
            [name]: value
        }));

        // Call fetchExpenses to update the data based on the new filters
        fetchExpenses();
    };


    // Fetch expenses from the backend
    const fetchExpenses = async () => {
        try {
            const response = await fetch('/expenses', {
                method: 'GET', // specify the GET method
                headers: {
                    'Content-Type': 'application/json' // specify the content type
                }
            });
            if (!response.ok) {
                throw new Error('Failed to fetch expenses');
            }
            const data = await response.json();
            return data;
        } catch (error) {
            console.error('Error fetching expenses:', error);
            return [];
        }
    };

    return (
        <div>
            <TextField
                name="userId"
                label="User ID1"
                value={filters.userId}
                onChange={handleFilterChange}
            />
            <TextField
                name="type"
                label="Type"
                value={filters.type}
                onChange={handleFilterChange}
            />
            <TextField
                name="startDate"
                label="Start Date"
                type="date"
                value={filters.startDate}
                onChange={handleFilterChange}
            />
            <TextField
                name="endDate"
                label="End Date"
                type="date"
                value={filters.endDate}
                onChange={handleFilterChange}
            />
            <Table>
                <TableHead>
                    <TableRow>
                        <TableCell>ID</TableCell>
                        <TableCell>User ID</TableCell>
                        <TableCell>Amount</TableCell>
                        <TableCell>Operation Type</TableCell>
                        <TableCell>Type</TableCell>
                        <TableCell>Date</TableCell>
                    </TableRow>
                </TableHead>
                <TableBody>
                    {filteredExpenses.map(expense => (
                        <TableRow key={expense.id}>
                            <TableCell>{expense.id}</TableCell>
                            <TableCell>{expense.userId}</TableCell>
                            <TableCell>{expense.amount}</TableCell>
                            <TableCell>{expense.operationType}</TableCell>
                            <TableCell>{expense.type}</TableCell>
                            <TableCell>{expense.date}</TableCell>
                        </TableRow>
                    ))}
                </TableBody>
            </Table>
        </div>
    );
}

export default ExpenseTable;
